package java181005;


public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char cha = 'A';
		System.out.println(cha); // returns A
		System.out.println((int)cha); // returns 65
		System.out.println(cha + 1); // returns 66
		System.out.println((char)(cha + 1)); // returns B
	}

}
